import React from 'react';

import Jumbo from '../components/Jumbo/index';

class Tshirts extends React.Component {
  state = {
    name: '',
    lastTwo: []
  };

  handleChange = event => {
    this.setState({ name: event.target.value });

    if (this.state.lastTwo.length < 2) {
      console.log('less than 2');
      var newStateArray = this.state.lastTwo.slice();
      newStateArray.push(event.key);
      this.setState({ lastTwo: newStateArray }, function() {
        console.log(this.state.lastTwo);
      });
    } else {
      console.log('more than 2 ' + this.state.lastTwo);
      this.state.lastTwo.splice(0, 1);

      newStateArray = this.state.lastTwo.slice();
      newStateArray.push(event.key);
      this.setState({ lastTwo: newStateArray }, function() {
        console.log(this.state.lastTwo);
      });
    }
  };

  componentDidMount() {
    this.updateCanvas();
  }
  updateCanvas() {
    const ctx = this.refs.canvas.getContext('2d');

    var imageObj1 = new Image();
    imageObj1.src = 'http://kbd-intl.narod.ru/images/en.png';
    imageObj1.onload = function() {
      ctx.drawImage(imageObj1, 0, 0);
    };
  }

  render() {
    return (
      <div>
        <Jumbo />
        <div className={'container-fluid'}>
          <div className={'row'}>
            <div className={'col-md d-flex justify-content-center'}>
              <canvas
                ref='canvas'
                className={'border'}
                id={'canvas'}
                width={725}
                height={250}
              />
            </div>
            <div className={'col-md'}>
              <h1>Enter Your Name:</h1>

              <form>
                <input
                  className={'form-control form-control-lg'}
                  onKeyPress={this.handleChange}
                />
              </form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Tshirts;
